SnippetCode.val:
using System;
using System.Collections.Generic;
using PushSharp.Google;
using Newtonsoft.Json.Linq;

public static class Snippet
{
	public static void SendToAndroid(ref PushSharp.Google.GcmServiceBroker gcmBroker, System.String deviceID, System.String title, System.String msg)
	{
		gcmBroker.QueueNotification (new GcmNotification() {
        	RegistrationIds = new List<string> { deviceID	}, 
			Data = JObject.Parse("{ \"title\" : \"" + title  + "\"," + " \"body\" : \"" + msg + "\"}")
    	});
	}
}

using System;
using PushSharp;
using PushSharp.Core;
using PushSharp.Apple;
using Newtonsoft.Json.Linq;

public static class Snippet
{
	public static void SendToiOS(ref PushSharp.Apple.ApnsServiceBroker apnsBroker, System.String deviceID, System.String msg)
	{
		apnsBroker.QueueNotification (new ApnsNotification {
        	DeviceToken = deviceID,
			Payload = JObject.Parse("{\"aps\":{\"alert\":\""+msg+"\",\"badge\":\"1\"}}")
    	});
	}
}

using System;
using System.Collections.Generic;
using PushSharp.Windows;
using System.Xml.Linq;

public static class Snippet
{
	public static void SendToWin10Mobile(ref PushSharp.Windows.WnsServiceBroker wnsBroker, System.String deviceID, System.String msg)
	{
		wnsBroker.QueueNotification (new WnsToastNotification {
			ChannelUri = deviceID,
        Payload = XElement.Parse (@"
            <toast activationType='foreground' launch='"+msg+@"'>
                <visual>
                    <binding template=""ToastText01"">
                        <text id=""1"">"+msg+@"</text>
                    </binding>  
                </visual>
            </toast>")
    	});
	}
}
